insert into tracks (strPath, strArtist, strAlbumArtist, strAlbum, strGenre, strTitle, iTrack, iNumTracks, iDuration, iYear, iTimesPlayed, iRating, iFavorite, iResumeAt, iDisc, iNumDisc, iGainTrack, iPeakTrack, strLyrics, musicBrainzID, dateLastPlayed, dateadded) select strPath || strFileName, strArtist || ' | ', '', strAlbum, strGenre || ' | ', strTitle, itrack, 0, iDuration, iyear, itimesplayed, irating, favorite, 0, 0, 0, 0, 0, '', '', '1900-01-01 00:00:00', dateadded from dbold.song,dbold.album,dbold.genre,dbold.artist,dbold.path where song.idPath=path.idPath and song.idAlbum=album.idAlbum and song.idGenre=genre.idGenre and song.idArtist=artist.idArtist;
insert into artist (strartist) select distinct strartist from dbold.artist;
insert into genre (strGenre) select distinct strGenre from dbold.genre;
insert into artistinfo (strArtist, strBorn, strYearsActive, strGenres, strTones, strStyles, strInstruments, strImage, strAMGBio, strAlbums, strCompilations, strSingles, strMisc) select strArtist, strBorn, strYearsActive, strGenres, strTones, strStyles, strInstruments, strImage, strAMGBio, strAlbums, strCompilations, strSingles, strMisc from dbold.artist, dbold.artistinfo where artistinfo.idartist = artist.idartist;
insert into albuminfo (strArtist, strAlbum, strTones, strStyles, strReview, strImage, strTracks, iYear, iRating) select strArtist, strAlbum, strTones, strStyles, strReview, strImage, strTracks, iYear, iRating from dbold.artist, dbold.album, dbold.albuminfo where albuminfo.idartist = artist.idartist and albuminfo.idalbum = album.idalbum;
insert into scrobbleusers select * from dbold.scrobbleusers;
insert into scrobblesettings (idScrobbleSettings, idScrobbleUser, iAddArtists, iAddTracks, iNeighbourMode, iRandomness, iScrobbleDefault, iSubmitOn, iDebugLog, iOfflineMode, iPlaylistLimit, iPreferCount, iRememberStartArtist, iAnnounce) select idScrobbleSettings, idScrobbleUser, iAddArtists, iAddTracks, iNeighbourMode, iRandomness, iScrobbleDefault, iSubmitOn, iDebugLog, iOfflineMode, iPlaylistLimit, iPreferCount, iRememberStartArtist, 0 from dbold.scrobblesettings;
insert into scrobblemode select * from dbold.scrobblemode;
insert into scrobbletags select * from dbold.scrobbletags;